import axios from 'axios';
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Button from '@material-ui/core/Button';

const Create = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');

  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    // ham yaha data ko send kar rahe hai means post kar rahe hai
    // button par submit karne par ye waala API hit hoga and ye name and email ka data waha jaakar mock server par save ho jaayega.

    axios
      .post('https://63f1fbf04f17278c9a1dcbf5.mockapi.io/crud-operation', {
        name: name,
        email: email,
      })
      .then(() => {
        navigate('/read'); // matlab ek baar axios aapke data ko send karde uske baad ye aapko navigate karde, .then lagane se ye confirm hai ki ye tabhi navigate karega jab aapka data send ho jaayega.
      });
  };
  // Note: is mockapi server par muje apna account banakar usme data ko send and retrive karna hai axios ke thru, ye abhi mane kisi or ka mockapi ka url diya hai.
  return (
    <>
      <div className="register-page">
        <div className="d-flex justify-content-between my-3">
          <h2 className="register-now">Register Now</h2>
          <Link to="/read">
            <Button variant="contained">Show Data</Button>
          </Link>
        </div>
        <form className="register-form">
          <div className="mb-3">
            <label className="form-label">Name</label>
            <input
              type="text"
              className="form-control"
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div className="mb-3">
            <label className="form-label">Email address</label>
            <input
              type="email"
              className="form-control"
              aria-describedby="emailHelp"
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>

          <button></button>
          <Button
            variant="contained"
            color="success"
            type="submit"
            className="btn btn-green"
            onClick={handleSubmit}
          >
            Submit
          </Button>
        </form>
      </div>
    </>
  );
};

export default Create;
