import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Button from '@material-ui/core/Button';
import DeleteIcon from '@material-ui/icons/Delete';
import EditIcon from '@material-ui/icons/Edit';

const Read = () => {
  const [data, setData] = useState([]);

  function getData() {
    axios
      .get('https://63f1fbf04f17278c9a1dcbf5.mockapi.io/crud-operation')
      .then((res) => {
        setData(res.data);
      });
  }

  const handleDelete = (id) => {
    axios
      .delete(
        `https://63f1fbf04f17278c9a1dcbf5.mockapi.io/crud-operation/${id}`
      )
      .then(() => {
        getData(); // delete hone ke baad hamne again getData() ko call kiya hai jisse jisse setData ke ander new res.data a jaaye jisme ye delete waala data nahi hoga.
      });
  };

  const setLocalStorage = (id, name, email) => {
    localStorage.setItem('id', id);
    localStorage.setItem('name', name);
    localStorage.setItem('email', email);
  };

  useEffect(() => {
    getData();
  }, []);

  return (
    <>
      <div className="d-flex justify-content-between m-3">
        <h2 className="register-now">Register Data</h2>
        <Link to="/">
          <Button variant="contained">Register</Button>
        </Link>
      </div>
      <div class="table-responsive">
        <table className="table table-condensed">
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col">Name</th>
              <th scope="col">Email</th>
              <th scope="col"></th>
              <th scope="col"></th>
            </tr>
          </thead>
          {data.map((eachdata) => {
            return (
              <>
                <tbody>
                  <tr>
                    <td>{eachdata.id}</td>
                    <td>{eachdata.name}</td>
                    <td>{eachdata.email}</td>
                    <td>
                      <Link to="/update">
                        <Button
                          className="edit-icon"
                          onClick={() =>
                            setLocalStorage(
                              eachdata.id,
                              eachdata.name,
                              eachdata.email
                            )
                          }
                        >
                          <EditIcon />
                        </Button>
                      </Link>
                      {/* {button par click karne par 2 operation perform ho rahe hai pehla hai onClick wala jisme ham dta ko localstorage me save kar rahe hai and then <Link> ke thru uodate page par redirect kar rahe hai} */}
                    </td>
                    <td>
                      <Button
                        className="delete-icon"
                        onClick={() => handleDelete(eachdata.id)}
                      >
                        <DeleteIcon />
                      </Button>
                    </td>
                  </tr>
                </tbody>
              </>
            );
          })}
        </table>
      </div>
    </>
  );
};

export default Read;
