import axios from 'axios';
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Create = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');

  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    // ham yaha data ko send kar rahe hai means post kar rahe hai
    // button par submit karne par ye waala API hit hoga and ye name and email ka data waha jaakar mock server par save ho jaayega.

    axios
      .post('https://62a59821b9b74f766a3c09a4.mockapi.io/crud-youtube', {
        name: name,
        email: email,
      })
      .then(() => {
        navigate('/read'); // matlab ek baar axios aapke data ko send karde uske baad ye aapko navigate karde, .then lagane se ye confirm hai ki ye tabhi navigate karega jab aapka data send ho jaayega.
      });
  };

  return (
    <>
      <div className="d-flex justify-content-between m-2">
        <h2>Create</h2>
        <Link to="/read">
          <button className="btn btn-primary">Show Data</button>
        </Link>
      </div>
      <form>
        <div className="mb-3">
          <label className="form-label">Name</label>
          <input
            type="text"
            className="form-control"
            onChange={(e) => setName(e.tar.value)}
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Email address</label>
          <input
            type="email"
            className="form-control"
            aria-describedby="emailHelp"
            onChange={(e) => setEmail(e.tar.value)}
          />
        </div>

        <button
          type="submit"
          className="btn btn-primary"
          onClick={handleSubmit}
        >
          Submit
        </button>
      </form>
    </>
  );
};

export default Create;
