import axios from 'axios';
import React, { useEffect, useState } from 'react';

const Read = () => {
  const [data, setData] = useState([]);

  function getData() {
    axios
      .get('https://62a59821b9b74f766a3c09a4.mockapi.io/crud-youtube')
      .then((res) => {
        setData(res.data);
      });
  }

  const handleDelete = (id) => {
    // ham filter method ki help se bhi delete operation perform kar sakte the.
    axios
      .delete(`https://62a59821b9b74f766a3c09a4.mockapi.io/crud-youtube\${id}`)
      .then(() => {
        getData();
      });
  };

  useEffect(() => {
    getData();
  }, []);

  return (
    <>
      <h2>Read Operation</h2>
      <table className="table">
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">Name</th>
            <th scope="col">Email</th>
            <th scope="col"></th>
            <th scope="col"></th>
          </tr>
        </thead>
        {data.map((eachdata) => {
          return (
            <>
              <tbody>
                <tr>
                  <tr scope="row">{eachdata.id}</tr>
                  <td>{eachdata.name}</td>
                  <td>{eachdata.email}</td>
                  <td>
                    <button className="btn-success">Edit</button>
                  </td>
                  <td>
                    <button
                      className="btn-danger"
                      onClick={() => handleDelete(eachdata.id)}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              </tbody>
            </>
          );
        })}
      </table>
    </>
  );
};

export default Read;
