import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
// import DarkModeIcon from '@material-ui/icons/DarkMode';
import DarkModeIcon from '@mui/icons-material/DarkMode';

const Read = () => {
  const [data, setData] = useState([]);
  const [tabledark, setTableDark] = useState(''); // table ko dark and light karne ke liye is hook ka use kiya hai.
  function getData() {
    axios
      .get('https://62a59821b9b74f766a3c09a4.mockapi.io/crud-youtube')
      .then((res) => {
        setData(res.data);
      });
  }

  const handleDelete = (id) => {
    axios
      .delete(`https://62a59821b9b74f766a3c09a4.mockapi.io/crud-youtube\${id}`)
      .then(() => {
        getData(); // delete hone ke baad hamne again getData() ko call kiya hai jisse jisse setData ke ander new res.data a jaaye jisme ye delete waala data nahi hoga.
      });
  };

  const setLocalStorage = (id, name, email) => {
    localStorage.setItem('id', id);
    localStorage.setItem('name', name);
    localStorage.setItem('email', email);
  };

  useEffect(() => {
    getData();
  }, []);

  return (
    <>
      <div className="form-check form-switch">
        {/* <input
          className="form-check-input"
          type="checkbox"
          onClick={() => {
            if (tabledark === 'table-dark') setTableDark('');
            else setTableDark('table-dark');
          }}
        /> */}
        <DarkModeIcon
          onClick={() => {
            if (tabledark === 'table-dark') setTableDark('');
            else setTableDark('table-dark');
          }}
        />
      </div>

      <div className="d-flex justify-content-between m-2">
        <h2>Read Operation</h2>
        <Link to="/">
          <button className="btn btn-secondary">Create</button>
        </Link>
      </div>
      <table className={`table ${tabledark} `}>
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">Name</th>
            <th scope="col">Email</th>
            <th scope="col"></th>
            <th scope="col"></th>
          </tr>
        </thead>
        {data.map((eachdata) => {
          return (
            <>
              <tbody>
                <tr>
                  <tr scope="row">{eachdata.id}</tr>
                  <td>{eachdata.name}</td>
                  <td>{eachdata.email}</td>
                  <td>
                    <Link to="/update">
                      <button
                        className="btn-success"
                        onClick={() => () =>
                          setLocalStorage(
                            eachdata.id,
                            eachdata.name,
                            eachdata.email
                          )}
                      >
                        Edit
                      </button>
                    </Link>
                    {/* {button par click karne par 2 operation perform ho rahe hai pehla hai onClick wala jisme ham dta ko localstorage me save kar rahe hai and then <Link> ke thru uodate page par redirect kar rahe hai} */}
                  </td>
                  <td>
                    <button
                      className="btn-danger"
                      onClick={() => handleDelete(eachdata.id)}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              </tbody>
            </>
          );
        })}
      </table>
    </>
  );
};

export default Read;
