import axios from 'axios';
import React, { useState } from 'react';

const Create = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // ham yaha data ko send kar rahe hai means post kar rahe hai
    // button par submit karne par ye waala API hit hoga and ye name and email ka data waha jaakar mock server par save ho jaayega.

    axios.post('https://62a59821b9b74f766a3c09a4.mockapi.io/crud-youtube', {
      name: name,
      email: email,
    });
  };

  return (
    <>
      <h2>Create</h2>
      <form>
        <div className="mb-3">
          <label className="form-label">Name</label>
          <input
            type="text"
            className="form-control"
            onChange={(e) => setName(e.tar.value)}
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Email address</label>
          <input
            type="email"
            className="form-control"
            aria-describedby="emailHelp"
            onChange={(e) => setEmail(e.tar.value)}
          />
        </div>

        <button
          type="submit"
          className="btn btn-primary"
          onClick={handleSubmit}
        >
          Submit
        </button>
      </form>
    </>
  );
};

export default Create;
