import React from 'react';

const Update = () => {
  // Ham data ko update 2 tarike se kar sakte hai, first tarika hai jisme ham database se direct data get karke usme update kare and dusra tarika hai jisme ham all data ko loalstorage me store kar lenege or phir is local storage se get karke usko update kar denge, or yaha ham local storage waala tarika he use kar rahe hai data ko update karne ke liye.

  return (
    <>
      <h2>Update</h2>
      <form>
        <div className="mb-3">
          <label className="form-label">Name</label>
          <input
            type="text"
            className="form-control"
            onChange={(e) => setName(e.tar.value)}
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Email address</label>
          <input
            type="email"
            className="form-control"
            aria-describedby="emailHelp"
            onChange={(e) => setEmail(e.tar.value)}
          />
        </div>

        <button
          type="submit"
          className="btn btn-primary"
          onClick={handleSubmit}
        >
          Update
        </button>
      </form>
    </>
  );
};

export default Update;
